<script setup lang="ts">interface PropsInterface {  items: any,  filter: any  enablePagination?: boolean  enableSearch?: boolean}const props = withDefaults(defineProps<PropsInterface>(), {  enablePagination: true,  enableSearch: true});const mounted = ref(false);const emit = defineEmits<{  (e: 'refreshItems'): void,}>()if (props.enableSearch) {  watch(() => props.filter.search, (newVariant, oldVariant) => {    if (newVariant !== oldVariant) {      props.filter.page = '1';      props.filter.search = newVariant;      refreshItems();    }  })}if (props.enablePagination) {  watch(() => props.filter.page, (newVariant, oldVariant) => {    props.filter.page = newVariant;    refreshItems();  })}const refreshItems = () => {  emit('refreshItems');}onMounted(() => {  mounted.value = true;});</script><template>  <div>    <teleport to="header #header_buttons" v-if="$slots.buttons && mounted">      <slot name="buttons"/>    </teleport>    <AdminUITableSearch v-if="enableSearch" v-model:search="filter.search"/>    <slot name="table"/>    <AdminUIPaginate v-if="enablePagination && items.data?.length && items.meta.total > items.meta.per_page"                     :items="items"                     v-model:page="filter.page"/>    <AdminUIAlert v-else-if="!items.data?.length" type="info">      {{ filter.search ? 'No items match your search words.' : 'No items have been added yet.' }}    </AdminUIAlert>  </div></template>