<script lang="ts" setup>type textAlignType = 'left' | 'center' | 'right'interface PropsInterface {  textAlign: textAlignType,  colWidth: number,}const props = defineProps<PropsInterface>();const colWidth = props.colWidth ? 'col-' + props.colWidth : 'col';const textAlign = props.textAlign ? 'text-' + props.textAlign : 'text-center';</script><template>  <div :class="[colWidth, textAlign]">    <slot/>  </div></template>