<script setup lang="ts">interface PropsInterface {  type?: 'primary' | 'info' | 'success' | 'danger',}const props = withDefaults(defineProps<PropsInterface>(), {  type: 'primary',});const classes = {  'primary': 'alert-primary',  'info': 'alert-info',  'success': 'alert-success',  'danger': 'alert-danger',};</script><template>  <div :class="['alert', classes[type]]">    <slot/>  </div></template>