<script setup lang="ts">interface PropsInterface {  color?: 'primary' | 'secondary' | 'info' | 'success' | 'danger',  size?: 'sm' | 'md' | 'lg',  icon?: string,  to?: string,}const props = withDefaults(defineProps<PropsInterface>(), {  color: 'primary',  size: 'sm',});const colors = {  'primary': 'btn-primary',  'secondary': 'btn-neutral',  'info': 'btn-info',  'success': 'btn-success',  'danger': 'btn-danger',};const sizes = {  'sm': 'btn-sm',  'md': 'btn-md',  'lg': 'btn-lg',};const is = props.to ? resolveComponent('NuxtLink') : 'button'</script><template>  <component      :is="is"      :to="props.to ? props.to : null"      :class="[       'btn',       colors[props.color],       sizes[props.size]      ]"  >    <i v-if="props.icon" :class="props.icon"></i>    <slot/>  </component></template>