<script lang="ts">export default {  inheritAttrs: false}</script><script setup lang="ts">interface PropsInterface {  id: string,  type?: 'text' | 'email' | 'password' | 'hidden',  modelValue?: any,  colWidth?: number,  label?: string,  error?: string,  icon?: string,}const props = withDefaults(defineProps<PropsInterface>(), {  type: 'text',  colWidth: 0,});const emit = defineEmits<{  (e: 'update:modelValue', value: any): void,  (e: 'changed', value: any): void,}>()const inputClass = {  'border-danger': props.error,  'form-control': true};const attr = {  type: props.type,  id: props.id,  value: props.modelValue,  class: inputClass,  ...useAttrs()}</script><template>  <AdminUIInputWrap :colWidth="colWidth">    <AdminUIInputLabel v-if="label" :inputID="id">      {{ label }}    </AdminUIInputLabel>    <template v-if="icon">      <div class="input-group position-relative">        <input            v-bind="attr"            @input="$emit('update:modelValue', $event.target.value)"        />        <span class="input-group-text">        <i :class="icon"></i>      </span>      </div>    </template>    <template v-else>      <input          v-bind="attr"          @input="$emit('update:modelValue', $event.target.value)"      />    </template>    <AdminUIInputError v-if="error"/>  </AdminUIInputWrap></template>