<script lang="ts">export default {  inheritAttrs: false}</script><script setup lang="ts">interface PropsInterface {  id: string,  type?: 'text' | 'email' | 'password' | 'hidden',  modelValue?: any,  colWidth?: number,  label?: string,  errors?: string[],  icon?: string,}const props = withDefaults(defineProps<PropsInterface>(), {  type: 'text',  colWidth: 0,  errors: null,});const emit = defineEmits<{  (e: 'update:modelValue', value: any): void,}>()const attr = reactive({  type: props.type,  id: props.id,  ...useAttrs()});</script><template>  <AdminUIInputWrap :colWidth="colWidth">    <AdminUIInputLabel v-if="label" :inputID="id">      {{ label }}    </AdminUIInputLabel>    <template v-if="icon">      <div class="input-group position-relative">        <input            :class="{'border-danger': props.errors?.length > 0,  'form-control': true}"            v-bind="attr"            v-model="modelValue"            @input="$emit('update:modelValue', $event.target.value)"        />        <span class="input-group-text">        <i :class="icon"></i>      </span>      </div>    </template>    <template v-else>      <input          :class="{'border-danger': props.errors?.length > 0,  'form-control': true}"          v-bind="attr"          v-model="modelValue"          @input="$emit('update:modelValue', $event.target.value)"      />    </template>    <AdminUIInputError :errors="errors"/>  </AdminUIInputWrap></template>