<script setup lang="ts">interface PropsInterface {  id: string,  type?: 'text' | 'email' | 'password' | 'hidden',  modelValue?: any  colWidth?: number,  label?: string,  error?: string,}const props = withDefaults(defineProps<PropsInterface>(), {  type: 'text',  colWidth: 0,});const emit = defineEmits<{  (e: 'update:modelValue', value: any): void,  (e: 'changed', value: any): void,}>()const inputClass = {  'border-danger': props.error,  'form-control form-control-sm': true};</script><template>  <AdminUIInputWrap :colWidth="colWidth">    <AdminUIInputLabel v-if="label" :inputID="id">      {{ label }}    </AdminUIInputLabel>    <input        :type="type"        :id="id"        :value="modelValue"        :class="inputClass"        @input="$emit('update:modelValue', $event.target.value)"        v-bind="$attrs"    />    <AdminUIInputError v-if="error"/>  </AdminUIInputWrap></template>