<script setup lang="ts">interface PropsInterface {  id: string  showModal: any  title: string  primaryBtnText?: string  secondaryBtnText?: string  size?: 'sm' | 'lg' | 'xl'}const props = withDefaults(defineProps<PropsInterface>(), {  primaryBtnText: 'Save',  secondaryBtnText: 'Close',  size: 'lg',});const emit = defineEmits<{  (e: 'primaryBtnClicked'): void,  (e: 'update:modalInstance', value: any): void,}>()const sizeClass = {  'sm': 'modal-sm',  'lg': 'modal-lg',  'xl': 'modal-xl',}[props.size];const modalInstance = ref();watch(() => props.showModal, (newVariant, oldVariant) => {  if (newVariant) {    modalInstance.value.show();  } else {    modalInstance.value.hide();  }})onMounted(() => {  modalInstance.value = new bootstrap.Modal(document.getElementById(props.id));})</script><template>  <div class="modal" :id="id" tabindex="-1" :aria-labelledby="id" aria-hidden="true">    <div :class="['modal-dialog modal-dialog-centered', sizeClass]">      <div class="modal-content shadow-3">        <div class="modal-header">          <h5 class="modal-title">{{ title }}</h5>          <div class="text-xs ms-auto">            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>          </div>        </div>        <div class="modal-body">          <slot/>        </div>        <div class="modal-footer">          <button type="button" class="btn btn-sm btn-neutral" data-bs-dismiss="modal">{{ secondaryBtnText }}</button>          <button @click.prevent="emit('primaryBtnClicked')" type="button" class="btn btn-sm btn-primary">{{ primaryBtnText }}</button>        </div>      </div>    </div>  </div></template>