<script lang="ts" setup>type textAlignType = 'left' | 'center' | 'right'interface PropsInterface {  textAlign?: textAlignType,  colWidth?: number,}const props = withDefaults(defineProps<PropsInterface>(), {  textAlign: 'center',  colWidth: 0});const colWidth = props.colWidth ? 'col-' + props.colWidth : 'col';const textAlign = 'text-' + props.textAlign;</script><template>  <div :class="[colWidth, textAlign]">    <slot/>  </div></template>