<script lang="ts" setup>type textAlignType = 'left' | 'center' | 'right'interface PropsInterface {  titles: {    text: string,    colWidth?: number    textAlign?: textAlignType  }[]}const props = defineProps<PropsInterface>();const textAlignClass = {  left: 'text-left',  center: 'text-center',  right: 'text-right',};</script><template>  <div class="row card_results_head">    <template v-for="title in props.titles">      <div          :class="[title.colWidth ? 'col-' + title.colWidth : 'col',                title.textAlign?textAlignClass[title.textAlign]:textAlignClass['center']]">        {{ title.text }}      </div>    </template>  </div></template><style scoped>.card_results_head {  margin-bottom: 20px;  margin-top: 16px;  padding-right: 1.25rem;  padding-left: 1.25rem;}@media only screen and (max-width: 940px) {  .card_results_head {    overflow: scroll;  }}</style>