<script lang="ts" setup>type textAlignType = 'left' | 'center' | 'right'interface PropsInterface {  textAlign?: textAlignType,  colWidth?: number,}const props = withDefaults(defineProps<PropsInterface>(), {  textAlign: 'center',  colWidth: 0});const textAlign = 'text-' + props.textAlign;</script><template>  <td :class="textAlign" :style="[colWidth?'width:' + colWidth + '%':'']">    <slot/>  </td></template>