<script setup lang="ts">const useAlert = useAlerts();const errors = ref<Record<string, string[]>>({});const route = useRoute();const itemID = route.params.id;const {data: item, pending, refresh: reFetch, error} = await useAsyncData(`user.${itemID}`, () =>    $larafetch('/users/' + itemID),);let data = item.value.data;let formData = reactive({  _method: 'PUT',  id: data.id,  name: data.name,  email: data.email,  passowrd: data.passowrd,  passowrd_confirmation: data.passowrd_confirmation,  avatar: data.avatar,});async function submitForm() {  errors.value = {};  try {    submitRequest(        $larafetch(`/users/${formData.id}`, {method: "post", body: formData}),        () => {          useAlert.success()        },        (validationErrors) => {          errors.value = validationErrors;        }    );  } catch (error) {    useAlert.error();  }}useHead({  title: data.name,})definePageMeta({  layout: "admin",});</script><template>  <div>    <form @submit.prevent="submitForm">      <AdminUICard>        <template #header>          <h3>Details</h3>        </template>        <div class="row">          <AdminUIInput id="name" label="User Name"                        v-model="formData.name"                        :errors="errors.name"                        :colWidth="12"/>          <AdminUIInput id="email" type="email" label="User Email"                        v-model="formData.email"                        :errors="errors.email"                        :colWidth="12"/>          <AdminUIInput id="password" type="password" label="New Password"                        :errors="errors.password"                        v-model="formData.password"                        :colWidth="6"/>          <AdminUIInput id="password_confirmation" type="password" label="Password Confirmation"                        v-model="formData.password_confirmation"                        :colWidth="6"/>          <AdminUIInputFile id="avatar" label="Profile Photo"                            isImg                            @change="formData.avatar = $event.target.files[0]"                            :errors="errors.avatar"                            :colWidth="12"/>        </div>      </AdminUICard>      <div class="text-center mt-5">        <AdminUIButton type="submit">Submit</AdminUIButton>      </div>    </form>  </div></template>